glm::vec3 CalculateTangent(glm::vec3& pos0, glm::vec3& pos1, glm::vec3& pos2, glm::vec2& uv0, glm::vec2& uv1, glm::vec2& uv2)
{
	glm::vec3 deltaPos1 = pos1 - pos0;
	glm::vec3 deltaPos2 = pos2 - pos0;

	glm::vec2 deltaUV1 = uv1 - uv0;
	glm::vec2 deltaUV2 = uv2 - uv0;

	float r = 1.0f / (deltaUV1.x * deltaUV2.y - deltaUV1.y * deltaUV2.x);
	return (deltaPos1 * deltaUV2.y - deltaPos2 * deltaUV1.y) * r;
}

glm::mat4 ConvertMatrixToGLMFormat(const aiMatrix4x4& from)
{
	glm::mat4 to;
	//the a,b,c,d in assimp is the row ; the 1,2,3,4 is the column
	to[0][0] = from.a1; to[1][0] = from.a2; to[2][0] = from.a3; to[3][0] = from.a4;
	to[0][1] = from.b1; to[1][1] = from.b2; to[2][1] = from.b3; to[3][1] = from.b4;
	to[0][2] = from.c1; to[1][2] = from.c2; to[2][2] = from.c3; to[3][2] = from.c4;
	to[0][3] = from.d1; to[1][3] = from.d2; to[2][3] = from.d3; to[3][3] = from.d4;
	return to;
}

Ref<Mesh> MeshLoader::Load(std::string path)
{
	if (path == "Sphere") return LoadSphereMesh();
	else if (path == "Cube") return LoadCube();
	else if (path == "Capsule") return LoadCapsule();
	else if (path == "Plane") return LoadPlane();
	else if (path == "Skybox") return LoadSkybox();
	else if (path == "Quad") return LoadQuad();
	else if (path == "Empty") return LoadEmpty();

	Assimp::Importer importer;
	//aiProcess_Triangulate			如果模型不是（全部）由三角形组成，它需要将模型所有的图元形状变换为三角形
	//aiProcess_FlipUVs				在处理的时候翻转y轴的纹理坐标
	//aiProcess_GenNormals			如果模型不包含法向量的话，就为每个顶点创建法线
	//aiProcess_SplitLargeMeshes	将比较大的网格分割成更小的子网格，如果你的渲染有最大顶点数限制，只能渲染较小的网格，那么它会非常有用
	//aiProcess_OptimizeMeshes		和上个选项相反，它会将多个小网格拼接为一个大的网格，减少绘制调用从而进行优化
	const aiScene* scene = importer.ReadFile(path, aiProcess_Triangulate | aiProcess_FlipUVs | aiProcess_CalcTangentSpace);

	//mFlags	返回的数据是不是不完整的
	if (!scene || scene->mFlags & AI_SCENE_FLAGS_INCOMPLETE || !scene->mRootNode)
	{
		std::cout << "ERROR::ASSIMP:: " << importer.GetErrorString() << std::endl;
		return nullptr;
	}

	Ref<Mesh> mesh = CreateRef<Mesh>();
	_boneInfoMap.clear();
	_boneCounter = 0;

	ParseAssimpNode(scene->mRootNode, scene, mesh);

	return mesh;
}

Ref<Mesh> MeshLoader::LoadSphereMesh()
{
	Ref<Mesh> mesh = CreateRef<Mesh>();

	Mesh::Data& data = mesh->AddMeshData(Mesh::Data());
	data.drawType = DrawType::TRIANGLE_STRIP;

	data.AddAttribute(VertexAttributeType::Position);
	data.AddAttribute(VertexAttributeType::Normal);
	data.AddAttribute(VertexAttributeType::Tangent);
	data.AddAttribute(VertexAttributeType::UV0);

	const unsigned int X_SEGMENTS = 32;
	const unsigned int Y_SEGMENTS = 32;

	bool oddRow = false;
	for (int y = 0; y < Y_SEGMENTS; ++y)
	{
		if (!oddRow) // even rows: y == 0, y == 2; and so on
		{
			for (int x = 0; x <= X_SEGMENTS; ++x)
			{
				data.indexData.push_back(y * (X_SEGMENTS + 1) + x);
				data.indexData.push_back((y + 1) * (X_SEGMENTS + 1) + x);
			}
		}
		else
		{
			for (int x = X_SEGMENTS; x >= 0; --x)
			{
				data.indexData.push_back((y + 1) * (X_SEGMENTS + 1) + x);
				data.indexData.push_back(y * (X_SEGMENTS + 1) + x);
			}
		}
		oddRow = !oddRow;
	}

	std::vector<glm::vec3> position;
	std::vector<glm::vec2> uv;

	for (unsigned int y = 0; y <= Y_SEGMENTS; ++y)
	{
		for (unsigned int x = 0; x <= X_SEGMENTS; ++x)
		{
			float	xSegment = (float)x / (float)X_SEGMENTS;
			float	ySegment = (float)y / (float)Y_SEGMENTS;
			float	xPos = std::cos(xSegment * 2.0f * Mathf::PI) * std::sin(ySegment * Mathf::PI);
			float	yPos = std::cos(ySegment * Mathf::PI);
			float	zPos = std::sin(xSegment * 2.0f * Mathf::PI) * std::sin(ySegment * Mathf::PI);

			mesh->UpdateBoundingBox(xPos, yPos, zPos);
			data.vertexData.insert(data.vertexData.end(), { xPos, yPos, zPos, xPos, yPos, zPos, 0.1f, 0.2f, 0.3f,  xSegment, ySegment });

			position.push_back(glm::vec3(xPos, yPos, zPos));
			uv.push_back(glm::vec2(xSegment, ySegment));
		}
	}

	for (auto itr = data.indexData.begin(); itr != data.indexData.end(); itr += 3)
	{
		unsigned int index0 = *(itr + 0);
		unsigned int index1 = *(itr + 1);
		unsigned int index2 = *(itr + 2);

		glm::vec3& pos0 = position[index0];
		glm::vec3& pos1 = position[index1];
		glm::vec3& pos2 = position[index2];

		glm::vec2& uv0 = uv[index0];
		glm::vec2& uv1 = uv[index1];
		glm::vec2& uv2 = uv[index2];

		glm::vec3 tangent = CalculateTangent(pos0, pos1, pos2, uv0, uv1, uv2);
	}

	return mesh;
}

